package com.kai.common.config;

import org.springframework.context.annotation.Bean;
import org.springframework.stereotype.Component;

import java.security.KeyPair;
import java.security.KeyPairGenerator;
import java.security.NoSuchAlgorithmException;
import java.security.SecureRandom;

@Component
public class GlobalBean {

    @Bean
    public String basePackage() {
        return "com.example.demo";
    }

    @Bean
    public String jwtSecretKey() {
        return "6A50A18D70FA63636645C65459F1D78";
    }

    @Bean
    public Integer jwtIntervalMinutes() {
        return 15;  //jwt，15分钟后过期
    }

    @Bean
    public String PublicKey() {
        return "MIGfMA0GCSqGSIb3DQEBAQUAA4GNADCBiQKBgQCcd+0zTY9Gn94iqkQJTlxYnEnCeFsLkk0a7hoAvi2B74VzDVV3xH0ZO9RkXvo1SgCB+uzbEWdrgQkzTqyjfTtgOguu3OnkVxIMJF34ibchTY0LWHGxq1m2gLGuVVqrlu1LtdV0X7xo/5zc8Mr+46veWb86kSpqe6rOAm69WWo5GwIDAQAB";
    }

    @Bean
    public String PrivateKey() {
        return "";
    }

    @Bean
    public KeyPair RSAKeyPair() {
        KeyPair keyPair = null;
        try {
            KeyPairGenerator kpg = KeyPairGenerator.getInstance("RSA");
            kpg.initialize(1024, new SecureRandom());
            keyPair = kpg.generateKeyPair();
        } catch (NoSuchAlgorithmException e) {
            e.printStackTrace();
        }

        return keyPair;
    }
}